sys_client_extension_point.example:
setTimeout(function () {
	var parent = this.top;
	if (!parent || parent.location.pathname != '/$studio.do') return;

	if (parent.x_itx_codesearch_fixed) return;

	var injector = top.angular.element(top.document).injector();
	injector.get('searchEndpoint').getTableOptions().then(function () {
		var dynamicSearchTables = injector.get('cacheService').get('codeSearchTables');
		var hardcodedTables = injector.get('SEARCH_TABLES');
		Array.prototype.push.apply(hardcodedTables, dynamicSearchTables
			.filter(function (t) { return !hardcodedTables.includes(t.name); })
			.map(function (t) { return t.name; })
		);

		// the tab manager fails to open some search results
		// in which case we resort to using the direct url open
		var tm = injector.get('tabManager');
		var ootbOpenSearchResult = tm.openSearchResult;
		tm.openSearchResult = function (name, table, sysId) {
			try {
				ootbOpenSearchResult.apply(this, arguments);
			} catch (e) {
				devStudioAPI.openStudioTab(table + '.do?sys_id=' + sysId);
			}
		};
		parent.x_itx_codesearch_fixed = true;
	});
});
