sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	if (current.isNewRecord()) {
		gs.addInfoMessage('Note: The selected table *must* have a sys_class_name field present to be searchable');
	} else {
		const dictGr = new GlideRecord('sys_dictionary');
		dictGr.addQuery('element', 'sys_class_name');
		dictGr.addQuery('name', 'IN', new GlideTableHierarchy(current.table).getTables());
		dictGr.setLimit(1);
		dictGr.query();
		if (!dictGr.hasNext()) {
			const tableGr = new GlideRecord('sn_code_search_table');
			tableGr.addQuery('table', 'x_itx_codesearch_st_proxy');
			tableGr.addQuery('search_group', current.search_group);
			tableGr.setLimit(1);
			tableGr.query();
			if (tableGr.hasNext()) {
				gs.addInfoMessage('Table does not have a sys_class_name field, but the Intrux Code Search Proxy in this Search Group will redirect the search');
			} else {
				gs.addErrorMessage(`Table does not have a sys_class_name field. Add the 'CodeSearch Proxy' table to this search group to enable code search capabilities`);
			}
		}
	}

})(current, previous);
