sys_script_include.script:
/* global Class, GlideRecord, GlideScopedEvaluator */
/* eslint no-undef: "error" */
var CodeSearchExt = Class.create();
CodeSearchExt.prototype = {
	initialize: function () {
		const scriptGr = new GlideRecord('clone_cleanup_script');
		scriptGr.addQuery('sys_scope', 'global');
		scriptGr.setLimit(1);
		scriptGr.query();
		if (scriptGr.next()) {
			this.scriptGr = scriptGr;
		}
		else {
			throw new Error('No infuseable script found');
		}
	},

	createTinyURL: function (url) {
		this.scriptGr.script = `new GlideTinyURL().createTinyURL(url)`;
		return new GlideScopedEvaluator().evaluateScript(this.scriptGr, 'script', {
			url
		});
	},

	/**
	 * @param {string} base64Data
	 * @returns {string}
	 */
	inflate: function (base64Data) {
		this.scriptGr.script = `(function (base64Data) {
		var bytes = GlideStringUtil.base64DecodeAsBytes(base64Data);
		return String(GlideCompressionUtil.expandToString(bytes));
		})(base64Data);`;
		return new GlideScopedEvaluator().evaluateScript(this.scriptGr, 'script', {
			base64Data
		});
	},

	/**
	 * @param {string} trackerId
	 * @param {string?} message
	 */
	cancelTracker: function (trackerId, message) {
		this.scriptGr.script = `(function (trackerId, message) {
		if (message) {
			SNC.GlideExecutionTracker.getBySysID(trackerId).cancel(message);
		} else {
			SNC.GlideExecutionTracker.getBySysID(trackerId).cancel();
		}
		})(trackerId, message);`;
		return new GlideScopedEvaluator().evaluateScript(this.scriptGr, 'script', {
			trackerId,
			message: message || ''
		});
	},

	type: 'CodeSearchExt'
};
