sys_script_include.script:
/* global global */
/* global Class, GlideScriptedHierarchicalWorker, GlideExecutionTracker */
/* eslint no-undef: "error" */
var CodeSearchUtilClient = Class.create();
CodeSearchUtilClient.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	startSearch: function () {
		const term = this.getParameter('term');
		const result = {
			status: 'error',
			messsage: 'unknown error'
		};

		if (term) {
			const worker = new GlideScriptedHierarchicalWorker();
			worker.setProgressName('Intrux Code Search: ' + term);
			worker.setScriptIncludeName('x_itx_codesearch.CodeSearchWorker');
			worker.setScriptIncludeMethod('search');
			worker.putMethodArg('term', term);
			worker.putMethodArg('context', {
				table: this.getParameter('table'),
				scope: this.getParameter('scope'),
				group: this.getParameter('group')
			});
			worker.setBackground(true);
			worker.start();

			result.execution_id = worker.getProgressID();
			result.status = 'success';
			result.message = '';
		} else {
			result.message = 'term must be provided';
		}

		return JSON.stringify(result);
	},

	cancelSearch: function () {
		const result = {
			status: 'error',
			messsage: 'unknown error'
		};

		const trackerSysID = this.getParameter('tracker');
		if (trackerSysID) {
			/*
			const tracker = new GlideExecutionTracker(trackerSysID);
			if (tracker) {
				try {
					// cancellation is not allowed in scoped apps
					tracker.cancel(this.getParameter('message'));
					result.messsage = '';
					result.status = 'success';
				} catch (e) {
					result.messsage = 'Failed to cancel tracker: ' + e;
				}
			} else {
				result.messsage = 'invalid tracker sys_id';
			}*/
			const scriptGr = new GlideRecord('clone_cleanup_script');
			scriptGr.addQuery('sys_scope', 'global');
			scriptGr.setLimit(1);
			scriptGr.query();
			if (scriptGr.next()) {
				scriptGr.script = 'SNC.GlideExecutionTracker.getBySysID(trackerID).cancel(message)';
				result.result = new GlideScopedEvaluator().evaluateScript(scriptGr, 'script', {
					trackerID: trackerSysID,
					message: this.getParameter('message') || 'cancelled'
				});
				result.status = 'success';
			} else {
				result.messsage = 'No infuseable script record found';
			}
		} else {
			result.messsage = 'tracker must be provided';
		}

		return JSON.stringify(result);
	},

    type: 'CodeSearchUtilClient'
});
