sys_script_include.script:
/* global global */
/* global Class, GlideScriptedHierarchicalWorker */
/* eslint no-undef: "error" */
var CodeSearchUtilClient = Class.create();
CodeSearchUtilClient.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	startSearch: function () {
		const term = this.getParameter('term');
		const result = {
			status: 'error',
			messsage: 'unknown error'
		};

		if (term) {
			const worker = new GlideScriptedHierarchicalWorker();
			worker.setProgressName('Intrux Code Search: ' + term);
			worker.setScriptIncludeName('x_itx_codesearch.CodeSearchWorker');
			worker.setScriptIncludeMethod('search');
			worker.putMethodArg('term', term);
			worker.putMethodArg('scope', this.getParameter('scope'));
			worker.putMethodArg('searchGroup', this.getParameter('search_group'));
			worker.setBackground(true);
			worker.start();

			result.execution_id = worker.getProgressID();
			result.status = 'success';
			result.message = '';
		} else {
			result.message = 'term must be provided';
		}

		return JSON.stringify(result);
	},

    type: 'CodeSearchUtilClient'
});
