sys_script_include.script:
/* global Set */
/* global gs, sn_scoped_cache, x_itx_codesearch */
/* global Class, GlideRecord, GlideAggregate, GlideTableHierarchy */
/* eslint no-undef: "error" */
var CodeSearchHandlerMetadata = Class.create();
CodeSearchHandlerMetadata.prototype = {
	initialize: function () {
		this.util = new x_itx_codesearch.Utilities();

		this.use_cache = gs.getProperty('x_itx_codesearch.use_cache') == 'true';
		if (this.use_cache) {
			// automatic cache clear when script is updated and the data structure changed
			this.cache_version = '1';
			this._checkCache('metadata_json_data');
		}
	},

	_checkCache: function (cache) {
		if (sn_scoped_cache.ScopedCacheManager.get(cache, 'cache_version') != this.cache_version) {
			sn_scoped_cache.ScopedCacheManager.flushScopedCache(cache);
			sn_scoped_cache.ScopedCacheManager.put(cache, 'cache_version', this.cache_version);
			gs.info(`clearing cache ${cache} - upgrading to cache version ${this.cache_version}`);
		}
	},

	/**
	 * tables this handler can search and serialize
	 * 
	 * @static
	 * @param {object} [context]
	 * @param {string} [context.table]
	 * @param {string} [context.group]
	 * @param {string} [context.scope]
	 * @returns {string[]}
	 */
	getHandledTables: function (context) {
		const ignoredTables = gs.getProperty('x_itx_codesearch.ignored_tables', '')
			.split(',')
			.map(x => x.trim())
			.filter(x => !!x);

		const searchedTables = [];
		if (context?.group) {
			const rootTables = new Set();
			const tablePaths = []; // each table's inheritance from root to table
			// In the context of a group search, tables (and their child tables)
			// which are part of a search group will be searched by the ootb
			// search api and will therefore not be searched by this handler
			const tableGr = new GlideRecord('sn_codesearch_table');
			tableGr.addQuery('search_group', context.group);
			tableGr.query();
			while (tableGr.next()) {
				// table search configurations might have been added prophylactically
				// for tables which do not yet exist on the platform
				const table = tableGr.table.toString();
				if (gs.tableExists(table)) {
					const hierarchy = new GlideTableHierarchy(table);
					tablePaths.push(hierarchy.getTables().reverse());
					rootTables.add(hierarchy.getRoot());
				}
			}

			const tree = {
				sys_metadata: this.util.getTableTree('sys_metadata')
			};

			// if the remote tables contained in overrides are present of the search group,
			// their corresponding search handler already takes care of properly searching
			// the underlying table (e.g. for flows), so this handler doesn't have to search them
			const overrides = {
				'x_itx_codesearch_st_flow': 'sys_hub_flow_block',
				'x_itx_codesearch_st_flow_action': 'sys_hub_action_type_base'
			};

			for (const [remoteTable, metadataTable] of Object.entries(overrides)) {
				if (rootTables.has(remoteTable)) {
					tablePaths.push(new GlideTableHierarchy(metadataTable).getTables().reverse());
				}
			}

			// remove the tables already covered from the tree
			tablePaths.forEach(path => {
				// walk the branch just before the table
				let step = tree[path[0]];
				for (let i = 1; i < path.length - 1 && step; i++) {
					step = step[path[i]];
				}

				if (step) {
					// remove the table and with it the branch
					delete step[path.at(-1)];
				}
			});

			searchedTables.push(...flatten(tree['sys_metadata']));
		} else if (context?.table) {
			const info = new GlideTableHierarchy(context.table);
			if (info.getRoot() == 'sys_metadata') {
				if (context.extensions) {
					searchedTables.push(...info.getAllExtensions());
				} else {
					// bypass the ignore and scope check:
					// - if the scope/application has no record of this table,
					//   the search will immediately return
					// - if the a ignored table was explicitly selected, do 
					//   not override the user selection (e.g. from async search)
					return [context.table];
				}
			}
		} else {
			searchedTables.push(...new GlideTableHierarchy('sys_metadata').getAllExtensions());
		}

		if (context?.scope) {
			const ga = new GlideAggregate('sys_metadata');
			ga.addQuery('sys_scope', context.scope);
			ga.groupBy('sys_class_name');
			ga.query();

			const tables = new Set();
			while (ga.next()) {
				tables.add(ga.getValue('sys_class_name'));
			}

			return searchedTables.filter(t => tables.has(t) && !ignoredTables.includes(t));
		}

		return searchedTables.filter(t => !ignoredTables.includes(t));

		function flatten(obj, arr = []) {
			for (const attr in obj) {
				arr.push(attr, ...flatten(obj[attr]));
			}

			return arr;
		}
	},

	/**
	 * test if this extensionpoint can handle the given table
	 * 
	 * @param {string} table
	 * @return {boolean}
	 */
	handlesTable: function (table) {
		return new GlideTableHierarchy(table).getRoot() == 'sys_metadata';
	},

	/**
	 * @param {string} table
	 * @param {string} term
	 * @param {object} options
	 * @returns {string[]}
	 */
	search: function (table, term, options) {
		const hits = new Set();
		// Note: Lookup is done on sys_metadata and ref_ fields to not generate cross scope privileges
		const metadataGr = new GlideRecord('sys_metadata');
		metadataGr.addQuery('sys_class_name', table);

		if (options?.scope) {
			metadataGr.addQuery('sys_scope', options.scope);
		}

		const variableFields = [];
		const cond = metadataGr.addQuery('sys_id', term);
		this.util.getTableFields(table).forEach(field => {
			if (field.type == 'glide_var') {
				variableFields.push(field);
			} else if (!field.name.startsWith('sys_')
				&& field.type != 'variable'
				&& !field.type.startsWith('journal')) {
				cond.addOrCondition('ref_' + field.table + '.' + field.name, 'CONTAINS', term);
			}
		});

		if (options?.limit) {
			metadataGr.setLimit(options.limit);
		}

		metadataGr.query();
		while (metadataGr.next()) {
			hits.add(metadataGr.getUniqueValue());
		}

		// Note: Related List Query on sys_variable_value did not work reliably
		if (variableFields.length) {
			const variableGr = new GlideRecord('x_itx_codesearch_metadata_variables');
			variableGr.addQuery('var_value', 'CONTAINS', term);
			variableGr.addQuery('meta_sys_class_name', table);

			if (options?.scope) {
				variableGr.addQuery('meta_sys_scope', options.scope);
			}

			variableGr.query();
			while (variableGr.next()) {
				hits.add(variableGr.getValue('meta_sys_id'));
			}
		}

		return [...hits];
	},

	/**
	 * Convert the given record to JSON data structure suitable to be used in a code search.
	 * 
	 * @param {GlideRecordGenerated} anyGr
	 * @param {object} options
	 * @returns {JSON}
	 */
	toJSON: function (anyGr, options) {
		const sysId = anyGr.getUniqueValue();
		if (this.use_cache) {
			const data = sn_scoped_cache.ScopedCacheManager.get('metadata_json_data', sysId);
			if (data) {
				return JSON.parse(data);
			}
		}

		const data = {
			'class': anyGr.getRecordClassName(),
			sys_id: anyGr.getUniqueValue(),
			field: this.util.toJSON(anyGr)
		};

		sn_scoped_cache.ScopedCacheManager.putRow('metadata_json_data', sysId, JSON.stringify(data), sysId);
		return data;
	},

	/**
	 * @param {string} table
	 * @param {GlideElement | GlideDateTime | string} since
	 * @param {object} context
	 * @param {string} [context.scope]
	 * @param {boolean} [context.extensions]
	 * @returns {boolean}
	 */
	updatedSince: function (table, since, context) {
		const metadataGr = new GlideRecord('sys_metadata');
		if (context.extensions) metadataGr.addQuery('sys_class_name', 'INSTANCEOF', table);
		else metadataGr.addQuery('sys_class_name', table);
		metadataGr.addQuery('sys_updated_on', '>', since);
		if (context.scope) metadataGr.addQuery('sys_scope', context.scope);
		metadataGr.setLimit(1);
		metadataGr.query();
		return metadataGr.hasNext();
	},

	type: 'CodeSearchHandlerMetadata'
};
