sys_script_include.script:
/* global Set */
/* global gs */
/* global Class, GlideRecord */
/* eslint no-undef: "error" */
var CodeSearchHandlerWorkflow = Class.create();
CodeSearchHandlerWorkflow.prototype = {
	initialize: function () {

	},

	/**
	 * tables this handler can search and serialize
	 * 
	 * @static
	 * @param {object} [context]
	 * @param {string} [context.scope]
	 * @returns {string[]}
	 */
	getHandledTables: function (context) {
		if (context?.table && context.table != 'wf_workflow') {
			return [];
		}

		if (context?.scope) {
			const wfGr = new GlideRecord('wf_workflow');
			wfGr.addQuery('sys_scope', context.scope);
			wfGr.setLimit(1);
			wfGr.query();
			if (!wfGr.hasNext()) {
				return [];
			}
		}

		return ['wf_workflow'];
	},

	/**
	 * test if this extensionpoint can search and serialize the given table
	 * 
	 * @param {string} table
	 * @return {boolean}
	 */
	handlesTable: function (table) {
		return table == 'wf_workflow';
	},

	/**
	 * get the SysIDs of records that match the record
	 * 
	 * @param {string} table
	 * @param {string} term
	 * @param {object} [options]
	 * @param {number} [options.limit]
	 * @returns {string[]}
	 */
	search: function (table, term, options) {
		const hits = new Set();
		const wfGr = new GlideRecord('wf_workflow');
		wfGr.addQuery('sys_id', term)
			.addOrCondition('name', 'CONTAINS', term)
			.addOrCondition('table', 'CONTAINS', term);

		if (options?.scope) {
			wfGr.addQuery('workflow.sys_scope', options.scope);
		}

		if (options?.limit) {
			wfGr.setLimit(options.limit);
		}

		wfGr.query();
		while (wfGr.next()) {
			hits.add(wfGr.getUniqueValue());
		}

		const wfVersionGr = new GlideRecord('wf_workflow_version');
		wfVersionGr.addQuery('sys_id', term)
			.addOrCondition('name', 'CONTAINS', term)
			.addOrCondition('description', 'CONTAINS', term)
			.addOrCondition('table', 'CONTAINS', term)
			.addOrCondition('condition', 'CONTAINS', term);

		if (options?.scope) {
			wfVersionGr.addQuery('workflow.sys_scope', options.scope);
		}

		if (options?.limit) {
			wfVersionGr.setLimit(options.limit);
		}

		wfVersionGr.addNotNullQuery('workflow');
		wfVersionGr.addQuery('workflow', 'NOT IN', [...hits]);
		wfVersionGr.query();		
		while (wfVersionGr.next()) {
			hits.add(wfVersionGr.getValue('workflow'));
		}

		const variableGr = new GlideRecord('sys_variable_value');
		variableGr.addQuery('document', 'wf_activity');
		variableGr.addQuery('value', 'CONTAINS', term);

		if (options?.limit) {
			variableGr.setLimit(options.limit);
		}

		variableGr.query();
		while (variableGr.next()) {
			const wfActivityGr = new GlideRecord('wf_activity');
			wfActivityGr.addQuery('sys_id', variableGr.document_key);
			if (options?.scope) {
				wfActivityGr.addQuery('workflow_version.workflow.sys_scope', options.scope);
			}

			wfActivityGr.addNotNullQuery('workflow_version.workflow');
			wfActivityGr.setLimit(1);
			wfActivityGr.query();
			if (wfActivityGr.next()) {
				hits.add(wfActivityGr.workflow_version.workflow.toString());
			}
		}

		return [...hits];
	},

	/**
	 * Convert the given record to JSON data structure suitable to be used in a code search.
	 * 
	 * @param {GlideRecordGenerated & wf_workflow} wfGr
	 * @param {object} options
	 * @returns {object}
	 */
	toJSON: function (wfGr, options) {
		const versions = [];
		const wfVersionGr = new GlideRecord('wf_workflow_version');
		wfVersionGr.addQuery('workflow', wfGr.sys_id);
		wfVersionGr.orderBy('order');
		wfVersionGr.query();
		while (wfVersionGr.next()) {
			const wfName = wfVersionGr.getDisplayValue() || wfVersionGr.getUniqueValue();
			const wfVersion = {
				name: wfName + (wfVersionGr.published ? ' (current version)' : ' (previous version)'),
				sys_id: wfVersionGr.getUniqueValue(),
				activities: []
			};

			const wfActivityGr = new GlideRecord('wf_activity');
			wfActivityGr.addQuery('workflow_version', wfVersionGr.sys_id);
			wfActivityGr.query();
			while (wfActivityGr.next()) {
				const activity = {
					name: 'Activity: ' + wfActivityGr.getDisplayValue() || wfActivityGr.getDisplayValue('activity_definition'),
					fields: []
				};
				const variableGr = new GlideRecord('sys_variable_value');
				variableGr.addQuery('document', 'wf_activity');
				variableGr.addQuery('document_key', wfActivityGr.sys_id);
				variableGr.query();
				while (variableGr.next()) {
					activity.fields.push({
						name: variableGr.getDisplayValue('variable'),
						value: variableGr.value.toString()
					});
				}

				wfVersion.activities.push(activity);
			}

			versions.push(wfVersion);
		}

		return {
			name: wfGr.name.toString(),
			description: wfGr.description.toString(),
			versions: versions
		};
	},

	type: 'CodeSearchHandlerWorkflow'
};
