sys_script_include.script:
/* global gs, sn_scoped_cache, x_itx_codesearch */
/* global Class, GlideRecord */
/* eslint no-undef: "error" */
var CodeSearchHandlerFlowAction = Class.create();
CodeSearchHandlerFlowAction.prototype = {
	initialize: function () {
		this.util = new x_itx_codesearch.Utilities();
		this.use_cache = gs.getProperty('x_itx_codesearch.use_cache') == 'true';
		if (this.use_cache) {
			// automatic cache clear when script is updated and the data structure changed
			this.cache_version = '1';
			this._checkCache('flow_action_json_data');
		}
	},

	_checkCache: function (cache) {
		if (sn_scoped_cache.ScopedCacheManager.get(cache, 'cache_version') != this.cache_version) {
			sn_scoped_cache.ScopedCacheManager.flushScopedCache(cache);
			sn_scoped_cache.ScopedCacheManager.put(cache, 'cache_version', this.cache_version);
			gs.info(`clearing cache ${cache} - upgrading to cache version ${this.cache_version}`);
		}
	},

	/**
	 * tables this handler is search and serialize
	 * 
	 * @static
	 * @param {object} [context]
	 * @param {string} [context.scope]
	 * @returns {string[]}
	 */
	getHandledTables: function (context) {
		if (context?.scope) {
			const actionGr = new GlideRecord('sys_hub_action_type_definition');
			actionGr.addQuery('sys_scope', context.scope);
			actionGr.setLimit(1);
			actionGr.query();
			if (!actionGr.hasNext()) {
				return [];
			}
		}

		return ['sys_hub_action_type_definition'];
	},

	/**
	 * test if this extensionpoint can search and serialize the given table
	 * 
	 * @param {string} table
	 * @return {boolean}
	 */
	handlesTable: function (table) {
		return table == 'sys_hub_action_type_definition';
	},

	/**
	 * get the SysIDs of records that match the record
	 * 
	 * @param {string} table
	 * @param {string} term
	 * @param {object} [options]
	 * @param {number} [options.limit]
	 * @returns {string[]}
	 */
	search: function (table, term, options) {
		const actionGr = new GlideRecord('x_itx_codesearch_st_flow_action');
		actionGr.addQuery('data', 'CONTAINS', term);
		if (options?.scope) {
			actionGr.addQuery('sys_scope', options.scope);
		}

		if (options?.limit) {
			actionGr.setLimit(options.limit);
		}

		const sysIDs = [];
		actionGr.query();
		while (actionGr.next()) {
			sysIDs.push(actionGr.getUniqueValue());
		}

		return sysIDs;
	},

	/**
	 * Serialize the given record into a JSON.stringify-able data structure
	 * suitable to be used in a code search.
	 * 
	 * @param {GlideRecordGenerated & sys_hub_action_type_definition} anyGr
	 * @param {object} [options]
	 * @returns {JSON}
	 */
	toJSON: function (actionGr, options) {
		const actionSysId = actionGr.getUniqueValue();
		if (this.use_cache) {
			const data = sn_scoped_cache.ScopedCacheManager.get('flow_action_json_data', actionSysId);
			if (data) {
				return JSON.parse(data);
			}
		}

		const steps = [];
		// Note: There is no need to order here as we need to do a numeric sort later (orderBy will do a lexicographic order)
		const stepGr = new GlideRecord('sys_hub_step_instance');
		stepGr.addQuery('action', actionSysId);
		stepGr.query();
		while (stepGr.next()) {
			const order = +stepGr.order.toString();
			steps.push({
				label: `Step ${order}`,
				order: order,
				fields: this.util.toJSON(stepGr)
			});
		}

		steps.sort((x, y) => x.order - y.order);
		const data = {
			sys_id: actionSysId,
			name: actionGr.name.toString(),
			description: actionGr.description.toString(),
			annotation: actionGr.annotation.toString(),
			steps: steps
		};

		sn_scoped_cache.ScopedCacheManager.putRow('flow_action_json_data', actionSysId, JSON.stringify(data), actionSysId);
		return data;
	},

	type: 'CodeSearchHandlerFlowAction'
};
