sys_script_include.script:
/* global gs, sn_scoped_cache */
/* global Set */
/* global Class, GlideScriptedExtensionPoint, GlideRecord, GlideTableHierarchy */
/* eslint no-undef: "error" */
var CodeSearchAPI = Class.create();
CodeSearchAPI.prototype = {
	initialize: function () {
		this.handlers = [];
		for (const handler of new GlideScriptedExtensionPoint().getExtensions('CodeSearchHandler')) {
			handler.initialize();
			this.handlers.push(handler);
		}
	},

	/**
	 * Returns the configuration (sys_metadata extension) tables
	 * which are not covered by the given search group.
	 * 
	 * @param {object} [params]
	 * @param {string} [params.table]
	 * @param {string} [params.group]
	 * @param {string} [params.scope]
	 * @returns {string[]}
	 */
	getSearchTables: function (params) {
		const tables = [];
		for (const handler of this.handlers) {
			tables.push(...handler.getHandledTables(params));
		}

		if (params?.group) {
			const tableGr = new GlideRecord('sn_codesearch_table');
			tableGr.addQuery('search_group', params.group);
			tableGr.query();
			while (tableGr.next()) {
				tables.push(tableGr.getValue('table'));
			}
		}

		return [...new Set(tables)];
	},

	/**
	 * @param {string} table
	 * @param {string} term
	 * @param {object} [options]
	 * @returns {string[]}
	 */
	search: function (table, term, options) {
		for (const handler of this.handlers) {
			if (handler.handlesTable(table)) {
				return handler.search(table, term, options);
			}
		}

		gs.warn(`no handler to search table '${table}' found`);
		return [];
	},

	/**
	 * @param {GlideRecordGenerated} anyGr
	 * @returns {object}
	 */
	toJSON: function (anyGr) {
		const klass = anyGr.getRecordClassName();
		for (const handler of this.handlers) {
			if (handler.handlesTable(klass)) {
				// promote the anyGr reference from potential base table to actual table
				if (klass != anyGr.getTableName()) {
					const record = anyGr.getElement('ref_' + klass);
					if (record) {
						const recordGr = record.getRefRecord();
						if (recordGr && recordGr.isValidRecord()) {
							anyGr = recordGr;
						}
					}
				}

				return handler.toJSON(anyGr);
			}
		}

		gs.info(`no handler to JSON'ify for '${klass}' found`);
		return null;
	},

	type: 'CodeSearchAPI'
};
