sys_script_vtable.script:
(function executeQuery(v_table, v_query) {

	/* global v_table, v_query */
	/* global gs, x_itx_codesearch */
	/* global GlideRecord, GlideTableHierarchy */
	/* eslint no-undef: "error" */

	const uri = gs.action.getGlideURI();
	const scope = uri.get('search_all_scopes') == 'false' ? uri.get('current_app') : '';
	const searchGroup = uri.get('search_group');
	const dataCondition = 'dataLIKEACEAccessValidator' || v_query.getCondition('data');
	const term = dataCondition?.includes('CONTAINS')
		? dataCondition.split('CONTAINS')[1]
		: dataCondition?.includes('LIKE')
		? dataCondition.split('LIKE')[1]
		: '';
	if (searchGroup && term) {
		const api = new x_itx_codesearch.CodeSearchAPI();
		const searchedTables = api.getSearchedTables(searchGroup);
		new GlideTableHierarchy('sys_metadata')
			.getTableExtensions()
			.filter(t => searchedTables.includes(t))
			.forEach(table => {
				const queryParts = [];
				const negatedParts = [];
				let doVariablesSearch = false;
				api.getTableFields(table).forEach(field => {
					if (field.type == 'glide_var' || field.type == 'variables') {
						doVariablesSearch = true;
					} else {
						const base = 'ref_' + table + '.' + field.name;
						queryParts.push(base + 'LIKE' + term);
						negatedParts.push(base + 'NOT LIKE' + term);
					}
				});

				const anyGr = new GlideRecord('sys_metadata');
				if (scope) {
					anyGr.addQuery('sys_scope');
				}
				anyGr.addEncodedQuery(queryParts.join('^OR'));
				anyGr.setLimit(2);
				while (anyGr.next()) {
					v_table.addRow({
						sys_id: anyGr.getUniqueValue(),
						sys_class_name: anyGr.getRecordClassName(),
						data: JSON.stringify(api.metadataToData(anyGr), null, 2)
					});
				}

				if (doVariablesSearch) {
					// Note: Related List Query on sys_variable_value does not work reliable
					new GlideTableHierarchy(table)
						.getAllExtensions()
						.forEach(table => {
							const anyGr = new GlideRecord('sys_variable_value');
							anyGr.addQuery('document', table);
							anyGr.addQuery('value', 'CONTAINS', term);
							anyGr.addJoinQuery(table, 'document_key', 'sys_id');
							anyGr.setLimit(2);
							while (anyGr.next()) {
								v_table.addRow({
									sys_id: anyGr.getUniqueValue(),
									sys_class_name: anyGr.getRecordClassName(),
									data: JSON.stringify(api.metadataToData(anyGr), null, 2)
								});
							}
						});
				}
			});
	} else {
		gs.addInfoMessage('This table will only evaluate in a code search context');
	}

  })(v_table, v_query);

sys_script_vtable.update_script:
(function executeUpdate(v_record, v_changed_fields) {
     // Parameters:
     //    v_record - a map of field names and values containing the sys_id of the record
     //          v_record.<field_name>                   - fields in the remote table GlideRecord
