sys_script_vtable.script:
(function executeQuery(v_table, v_query) {

	/* global gs, x_itx_codesearch */
	/* global GlideRecord, GlideTableHierarchy */
	/* eslint no-undef: "error" */

	const uri = gs.action.getGlideURI();
	const groupName = uri.get('search_group');
	const scope = uri.get('search_all_scopes') == 'false' ? uri.get('current_app') : '';
	const dataCondition = v_query.getCondition('data');
	const term = dataCondition?.startsWith('dataCONTAINS')
		? dataCondition.substring('dataCONTAINS'.length)
		: dataCondition?.startsWith('dataLIKE')
		? dataCondition.substring('dataLIKE'.length)
		: '';

	const tableCondition = v_query.getCondition('sys_class_name');
	const table = tableCondition?.startsWith('sys_class_name=')
		? tableCondition.substring('sys_class_name='.length)
		: '';

	const sysIdCondition = v_query.getCondition('sys_id');
	const sysId = sysIdCondition?.startsWith('sys_id=')
		? sysIdCondition.substring('sys_id='.length)
		: '';

	const directTable = uri.get('itx_codesearch_table') || table;
	const directSysID = uri.get('itx_codesearch_sys_id') || sysId;

	if (directTable && directSysID) {
		const handler = new x_itx_codesearch.CodeSearchHandlerAny();
		handler.batchToJSON(directTable, [directSysID]).forEach(record => {
			v_table.addRow({
				sys_id: record.sys_id,
				sys_class_name: record.sys_class_name,
				sys_scope: record.sys_scope,
				record: record.sys_id,
				data: JSON.stringify(record.fields, null, 2),
				actions: 'c838fd22c371f690de14d8477d013160'
			});
		});
		return;
	}

	if (term) {
		let groupSysID = '';
		if (groupName) {
			const groupGr = new GlideRecord('sn_codesearch_search_group');
			groupGr.addQuery('name', groupName);
			groupGr.setLimit(1);
			groupGr.query();
			if (groupGr.next()) {
				groupSysID = groupGr.getUniqueValue();
			}
		}

		const handler = new x_itx_codesearch.CodeSearchHandlerAny();
		const tables = handler.getHandledTables({
			group: groupSysID,
			scope: scope,
			table: table
		});
		tables.forEach(table => {
			const hits = handler.search(table, term, {
				scope: scope
			});
			handler.batchToJSON(table, hits).forEach(record => {
				v_table.addRow({
					sys_id: record.sys_id,
					sys_class_name: record.sys_class_name,
					sys_scope: record.sys_scope,
					record: record.sys_id,
					data: JSON.stringify(record.fields, null, 2),
					actions: 'c838fd22c371f690de14d8477d013160'
				});
			});
		});

		return;
	}

  })(v_table, v_query); /* global v_table, v_query */

