sys_script_vtable.script:
(function executeQuery(v_table, v_query) {

	/* global x_itx_codesearch */
	/* global GlideRecord */
	/* eslint no-undef: "error" */

	// flow2json for Flow Designer Flows and Subflows
	//
	// due to performance reasons, non-filtered queries will be limited to:
	let limit = 100; // gs.getProperty('glide.script.vtable.max_rows', '1000')

	const util = new x_itx_codesearch.Utilities();
	const query = util.transformRemoteTableQuery('flow', v_query.getEncodedQuery() + '');

	// it might be actually beneficial to search sys_hub_flow_base because this will search previous
	// versions of flows - but currently this will make the same flow be appearing multiple times
	// TODO: figure out how to skip irrelevant flow snapshots
	const flowGr = new GlideRecord('sys_hub_flow');
	if (query) {
		flowGr.addEncodedQuery(query);
	}

	if (/^(flow\.)?sys_id=[a-z0-9]{32}$/.test(query)) {
		// optimize direct SysID lookups
		flowGr.setLimit(1);
	} else if (limit != -1) {
		flowGr.setLimit(limit);
	}

	flowGr.orderByDesc('sys_updated_on');
	flowGr.query();

	const handler = new x_itx_codesearch.CodeSearchHandlerFlow();
	while (flowGr.next()) {
		const data = handler.toJSON(flowGr);
		const dataStr = JSON.stringify(data, null, 4);
		v_table.addRow({
			sys_id: data.sys_id,
			sys_class_name: flowGr.getRecordClassName(),
			sys_scope: flowGr.getValue('sys_scope'),
			flow: data.sys_id,
			data: dataStr,
			actions: 'c838fd22c371f690de14d8477d013160'
		});
	}

  })(v_table, v_query); /* global v_table, v_query */

