     //      v_record.setLastErrorMessage(message);
     //  }
  })(v_record);

sys_script_vtable.script:
(function executeQuery(v_table, v_query) {

	/* global gs, sn_scoped_cache, x_itx_codesearch */
	/* global GlideRecord, GlideTableHierarchy, GlideStringUtil */
	/* eslint no-undef: "error" */

	const api = new x_itx_codesearch.CodeSearchAPI();
	if (v_query.isGet()) {
		const sysId = v_query.getSysId();
		if (GlideStringUtil.isEligibleSysID(sysId)) {
			// Use Case: "Show Data" (UI Action)
			const metadataGr = new GlideRecord('sys_metadata');
			metadataGr.addQuery('sys_id', sysId);
			metadataGr.setLimit(1);
			metadataGr.query();
			if (metadataGr.next()) {
				const data = api.toJSON(metadataGr);
				v_table.addRow({
					sys_id: sysId,
					record: sysId,
					sys_scope: metadataGr.getValue('sys_scope'),
					sys_class_name: metadataGr.getRecordClassName(),
					data: JSON.stringify(data, null, 2),
					actions: 'c838fd22c371f690de14d8477d013160'
				});
			}
		} else {
			//
			// Use Case: "Code Search Dummy Record"
			// The sys_id contains the search config which stores
			// search term, search group and scope.
			// The "Start Search" UI Action can be used to start the CodeSearchWorker
			// which does the heavy lifting in the background
			//
			v_table.addRow({
				sys_id: sysId,
				sys_class_name: 'x_itx_codesearch_st_metadata',
				data: JSON.stringify(JSON.parse(sysId), null, 2),
				actions: 'c838fd22c371f690de14d8477d013160'
			});
		}

		return;
	}

	const dataCondition = v_query.getCondition('data');
	const term = dataCondition?.startsWith('dataCONTAINS')
		? dataCondition.substring('dataCONTAINS'.length)
		: dataCondition?.startsWith('dataLIKE')
		? dataCondition.substring('dataLIKE'.length)
		: '';

	if (!term) {
		gs.addInfoMessage('Use [Data][contains][term] to start a code search');
		return;
	}

	// Code Search context is identified via gs.action:
	// api/sn_codesearch/v1/code_search/search
	// ?api=api&current_app=<app_sys_id>&limit=<limit>
	// &searchId=27&search_group=sn_devstudio.Studio+Search+Group
	// &table=<table>&term=<term>
	const searchConfig = (() => {
		const uri = gs.action.getGlideURI();
		const groupSysID = (() => {
			const groupName = uri.get('search_group');
			if (groupName) {
				const groupGr = new GlideRecord('sn_codesearch_search_group');
				groupGr.addQuery('name', groupName);
				groupGr.setLimit(1);
				groupGr.query();
				if (groupGr.next()) {
					return groupGr.getUniqueValue();
				}
			}

			return '';
		})();

		if (groupSysID) {
			//
			// Use Case: "Code Search"
			//
			return {
				term: term,
				scope: (() => {
					if (uri.get('search_all_scopes') == 'true') {
						return '';
					}

					return uri.get('current_app') || '';
				})(),
				group: groupSysID
			};
		}

		//
		// Use Case: "Manual Search"
		//
		return {
			term: term,
			table: (() => {
				const tableCondition = v_query.getCondition('sys_class_name');
				return tableCondition?.startsWith('sys_class_name=')
					? tableCondition.substring('sys_class_name='.length)
					: null;
			})(),
			scope: (() => {
				const scopeCondition = v_query.getCondition('sys_scope');
				return scopeCondition?.startsWith('sys_scope=')
					? scopeCondition.substring('sys_scope='.length)
					: null;
			})()
		};
	})();

	if (searchConfig.scope && searchConfig.scope != 'global') {
		// The processing time to perform an actual code search is acceptable ...
		const handler = new x_itx_codesearch.CodeSearchHandlerMetadata();
		const tables = handler.getHandledTables({
			group: searchConfig.group,
			table: searchConfig.table,
			scope: searchConfig.scope
		});
		tables.forEach(table => {
			const hits = api.search(table, term, {
				scope: searchConfig.scope
			});
			// always use metadata table to not create cross scope privileges
			const metadataGr = new GlideRecord('sys_metadata');
			metadataGr.addQuery('sys_id', 'IN', hits);
			metadataGr.query();
			while (metadataGr.next()) {
				const sysId = metadataGr.getUniqueValue();
				v_table.addRow({
					sys_id: sysId,
					record: sysId,
					sys_class_name: metadataGr.getRecordClassName(),
					data: JSON.stringify(api.toJSON(metadataGr), null, 2),
					sys_scope: metadataGr.getValue('sys_scope'),
					actions: 'c838fd22c371f690de14d8477d013160'
				});
			}
		});

		return;
	}

	// ... otherwise provide the dummy record
	v_table.addRow({
		sys_id: JSON.stringify(searchConfig),
		data: JSON.stringify({
			_: `Use the 'Start Search' on this record or specify an Application for direct search`,
			...searchConfig,
		}, null, 4),
		sys_class_name: 'x_itx_codesearch_st_metadata',
		sys_scope: searchConfig.scope,
		actions: '4027b57cc3a9fe90de14d8477d0131ac'
	});

	/* v_table.addRow({
			sys_id: JSON.stringify(searchConfig),
			data: JSON.stringify({
				_: `Use the 'Start Search' UI Action on this record to search all application files`,
				...searchConfig,
			}, null, 4),
			sys_class_name: 'x_itx_codesearch_st_metadata',
			sys_scope: searchConfig.scope,
			actions: 'c838fd22c371f690de14d8477d013160'
		}); */

  })(v_table, v_query); // eslint-disable-line

sys_script_vtable.update_script:
(function executeUpdate(v_record, v_changed_fields) {
     // Parameters:
     //    v_record - a map of field names and values containing the sys_id of the record
     //          v_record.<field_name>                   - fields in the remote table GlideRecord
     //          v_record.setLastErrorMessage(message)   - signal an error
     //    v_changed_fields - a map of field names and values containing the sys_id of the
     //          v_changed_fields.<field_name>           - changed fields in the remote table GlideRecord

     // Sample code:
     //  try {
     //      update-external-system;
     //      if (there-was-an-error) {
     //          var message = ...;
     //          v_record.setLastErrorMessage(message);
     //      }
     //  } catch (ex) {
     //      var message = ex.getMessage();
     //      v_record.setLastErrorMessage(message);
     //  }
  })(v_record, v_changed_fields);
