sys_script_vtable.script:
(function executeQuery(v_table, v_query) {

	/* global v_table, v_query */
	/* global gs, sn_scoped_cache, x_itx_codesearch */
	/* global GlideRecord, GlideTableHierarchy */
	/* eslint no-undef: "error" */

	const { source, sys_id, searchGroup, scope, term } = (() => {
		if (v_query.isGet()) {
			const cache = sn_scoped_cache.ScopedCacheManager.get('codesearch_lookup', v_query.getSysId());
			if (cache) {
				return {
					source: 'cache',
					sys_id: v_query.getSysId(),
					...JSON.parse(cache)
				};
			}
		}

		const dataCondition = v_query.getCondition('data');
		const term = dataCondition?.startsWith('dataCONTAINS')
			? dataCondition.substring('dataCONTAINS'.length)
			: dataCondition?.startsWith('dataLIKE')
			? dataCondition.substring('dataLIKE'.length)
			: '';

		const uri = gs.action.getGlideURI();
		const searchGroup = uri.get('search_group');
		const scope = uri.get('search_all_scopes') == 'false' ? uri.get('current_app') : '';
		if (searchGroup) {
			return {
				source: 'search',
				sys_id: gs.generateGUID(),
				searchGroup: searchGroup,
				scope: scope,
				term: term
			};
		}

		return {
			source: 'manual',
			sys_id: gs.generateGUID(),
			searchGroup: null,
			scope: '',
			term: term
		};
	})();

	const data = JSON.stringify({
		scope: scope,
		term: term,
		searchGroup: searchGroup
	});

	if (source == 'cache') {
		// replicate data from cache
	} else if (source == 'search') {
		// put data in cache so it can be looked up by direct call later
		sn_scoped_cache.ScopedCacheManager.put('codesearch_lookup', sys_id, data);
	} else if (source == 'manual') {
		if (term) {
			sn_scoped_cache.ScopedCacheManager.put('codesearch_lookup', sys_id, data);
		} else {
			return;
		}
	}

	v_table.addRow({
		sys_id: sys_id,
		sys_scope: scope || 'global',
		sys_class_name: 'x_itx_codesearch_st_metadata',
		data: data,
		actions: '3c77c024c3e1fa90de14d8477d0131b7'
	});

  })(v_table, v_query);

