sys_script_vtable.script:
(function executeQuery(v_table, v_query) {

	/* global v_table, v_query */
	/* global x_itx_codesearch */
	/* global GlideRecord */
	/* eslint no-undef: "error" */

	// action2json for Flow Designer Actions
	//
	// due to performance reasons, non-filtered queries will be limited to:
	let limit = 100; // gs.getProperty('glide.script.vtable.max_rows', '1000')

	const util = new x_itx_codesearch.Utilities();
	const query = util.transformRemoteTableQuery('action', v_query.getEncodedQuery());

	// not searching the base table (=> skipping snapshots)
	// for the same reason flow snapshots are (currently) not searched
	// see description in Remote Table Definition for Flows (ITX Flows)
	const actionGr = new GlideRecord('sys_hub_action_type_definition');
	if (query) {
		actionGr.addEncodedQuery(query);
	}

	if (/^(action\.)?sys_id=[a-z0-9]{32}$/.test(query)) {
		// optimize direct SysID lookups
		actionGr.setLimit(1);
	} else if (limit != -1) {
		actionGr.setLimit(limit);
	}

	actionGr.orderByDesc('sys_updated_on');
	actionGr.query();
	const handler = new x_itx_codesearch.CodeSearchHandlerFlowAction();
	while (actionGr.next()) {
		const data = handler.toJSON(actionGr);
		const dataStr = JSON.stringify(data, null, 4);
		v_table.addRow({
			sys_id: data.sys_id,
			sys_class_name: actionGr.getRecordClassName(),
			sys_scope: actionGr.getValue('sys_scope'),
			action: data.sys_id,
			data: dataStr,
			actions: 'c838fd22c371f690de14d8477d013160'
		});
	}

  })(v_table, v_query);

