sys_ui_action.script:
function openDataRecord() {
	var klass = g_list.getRow(g_sysId).getAttribute('record_class');
	var gm = new GlideModalForm('Data', g_list.getTableName());
	gm.setOnloadCallback(function (gm) {
		// ootb maximizeHeight has a but where $modalContent
		// is not set and height maximizing also doesn't work
		// because the iframe's height is not maximized
		gm.$modalContent = gm.$window.find('.modal-content');
		gm.$modalContent.find('iframe').height('100%');
		gm.maximizeHeight();
	});
	gm.setSysID(g_sysId);
	gm.render();
}
