sys_ui_page.client_script:
function codeSearch(term, scope) {
	if (term) {
		const ga = new GlideAjax('x_itx_codesearch.CodeSearchUtilClient');
		ga.addParam('sysparm_name', 'startSearch');
		ga.addParam('term', term);
		ga.addParam('scope', scope);
		ga.getXMLAnswer(function (result) {
			result = JSON.parse(result);
			if (result.status == 'success') {
				const dd = new GlideDialogWindow('hierarchical_progress_viewer', false, '40em', '10em');
				dd.setTitle('Code Search for \'' + term + '\'');
				dd.setPreference('sysparm_renderer_execution_id', result.execution_id);
				dd.render();
			} else if (result.message) {
				GlideUI.get().addOutputMessage({ type: 'error', msg: result.message });
			} else {
				GlideUI.get().addOutputMessage({ type: 'error', msg: 'Code Search initialization failed' });
			}
		});
	}

	return false;
}
