sys_ui_page.client_script:
/* global document, GlideAjax, GlideModal, GlideUI */
/* eslint no-undef: "error" */
(function () {
	var description = document.getElementById('searchgroup-description');
	document.querySelector('select#searchgroup').addEventListener('change', function () {
		description.innerText = this.selectedOptions[0].dataset.description || '';
		description.style.marginTop = description.innerText ? '1rem' : '';
		var visibleContainerId = 'table-' + this.selectedOptions[0].value + '-container';
		document.querySelectorAll('#table-select-container .table-container').forEach(function (container) {
			if (container.id == visibleContainerId) {
				container.style.display = '';
			} else {
				container.style.display = 'none';
			}
		});
	});
})();

function clearSelections() {	
	return false;
}

function getResults() {
	const term = document.getElementById('term').value;
	if (term) {
		const errHandler = function (msg) {
			GlideUI.get().addOutputMessage({ type: 'error', msg: msg });
		};

		var scope = document.getElementById('scope').value;
		var group = document.getElementById('searchgroup').value;
		var tableSysId = document.getElementById('table-' + group).value;
		if (tableSysId) {
			fetch('/api/now/table/sys_db_object/' + tableSysId + '?sysparm_fields=name', {
				headers: {
					'X-UserToken': g_ck
				}
			}).then(response => response.json()).then(json => {
				startSearch(term, scope, group == 'none' ? '' : group, json.result.name, errHandler);
			});
		} else if (group != 'none') {
			startSearch(term, scope, group, null, errHandler);
		}
	}

	return false;
}

function startSearch(term, scope, group, table, errHandler) {
	const ga = new GlideAjax('x_itx_codesearch.CodeSearchUtilClient');
	ga.addParam('sysparm_name', 'startSearch');
	ga.addParam('term', term);
	ga.addParam('scope', scope);
	ga.addParam('group', group);
	ga.addParam('table', table);
	ga.getXMLAnswer(function (result) {
		result = JSON.parse(result);
		if (result?.status != 'success') {
			errHandler(result?.message || 'Unknown error on code search start');
			return;
		}
		const dd = new GlideModal('hierarchical_progress_viewer', false, '40em', '10em');
		dd.setTitle('Code Search for \'' + term + '\'');
		dd.setPreference('sysparm_renderer_execution_id', result.execution_id);

		let completed = false;
		dd.on('executionComplete', function () {
			completed = true;
		});
		dd.on('beforeclose', function () {
			if (!completed) {
				const ga = new GlideAjax('x_itx_codesearch.CodeSearchUtilClient');
				ga.addParam('sysparm_name', 'cancelSearch');
				ga.addParam('tracker', result.execution_id);
				ga.addParam('message', 'cancelled (window closed)');
				ga.getXMLAnswer(function (result) {
					if (!result?.status == 'success') {
						errHandler(result?.message || 'Unknown error during search cancellation');
					}
				});
			}
		});
		dd.render();
	});
}
